import React, { useEffect, useState } from "react";
import Sidebar from "../../compoments/Sidebar";
import Table from "react-bootstrap/Table";
import ProductAPI from "../../../API/ProductAPI";
import queryString from "query-string";
import Pagination from "../../../Shop/Component/Pagination";

function ProductAdmin() {
  const [products, setProducts] = useState([]);

  const [pagination, setPagination] = useState({
    page: "1",
    count: "8",
    search: "",
    category: "all",
  });

  const [search, setSearch] = useState("");

  const onChangeText = (e) => {
    const value = e.target.value;

    setPagination({
      page: pagination.page,
      count: pagination.count,
      search: value,
      category: pagination.category,
    });
  };

  //Tổng số trang
  const [totalPage, setTotalPage] = useState();

  //Hàm này dùng để thay đổi state pagination.page
  //Nó sẽ truyền xuống Component con và nhận dữ liệu từ Component con truyền lên
  const handlerChangePage = (value) => {
    console.log("Value: ", value);

    //Sau đó set lại cái pagination để gọi chạy làm useEffect gọi lại API pagination
    setPagination({
      page: value,
      count: pagination.count,
      search: pagination.search,
      category: pagination.category,
    });
  };

  //Gọi hàm useEffect tìm tổng số sản phẩm để tính tổng số trang
  //Và nó phụ thuộc và state pagination
  useEffect(() => {
    const fetchAllData = async () => {
      const response = await ProductAPI.getAPI();
      console.log(response);

      //Tính tổng số trang = tổng số sản phẩm / số lượng sản phẩm 1 trang
      const totalPage = Math.ceil(
        parseInt(response.length) / parseInt(pagination.count)
      );
      console.log(totalPage);

      setTotalPage(totalPage);
    };

    fetchAllData();
  }, [pagination]);

  //Gọi hàm Pagination
  useEffect(() => {
    const fetchData = async () => {
      const params = {
        page: pagination.page,
        count: pagination.count,
        search: pagination.search,
        category: pagination.category,
      };

      const query = queryString.stringify(params);

      const newQuery = "?" + query;

      const response = await ProductAPI.getPagination(newQuery);
      console.log(response);

      setProducts(response);
    };
    // console.log(products);
    fetchData();
  }, [pagination]);

  return (
    <div className="d-flex justify-content-center">
      <div className="container row ">
        <Sidebar />
        <div className="col-10 w-100">
          <h4>Products</h4>
          <input
            className="form-control w-25 mb-3"
            onChange={onChangeText}
            type="text"
            placeholder="Enter Search!"
          />
          <div className="w-100">
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Price</th>
                  <th>Image</th>
                  <th>Category</th>
                  <th>Edit</th>
                </tr>
              </thead>
              <tbody>
                {products &&
                  products?.results?.map((product) => {
                    return (
                      <tr key={product._id}>
                        <td>{product._id}</td>
                        <td>{product.name}</td>
                        <td>{product.price}</td>
                        <td>
                          <img
                            src={product.photos[0]}
                            style={{ height: "60px", width: "60px" }}
                          />
                        </td>
                        <td>{product.category}</td>
                        <td>
                          <div className="d-flex flex-column gap-2">
                            <button className="btn btn-success mb-2">
                              Update
                            </button>
                            <button className="btn btn-danger">Delete</button>
                          </div>
                        </td>
                      </tr>
                    );
                  })}
              </tbody>
            </Table>
            <Pagination
              pagination={pagination}
              handlerChangePage={handlerChangePage}
              totalPage={totalPage}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductAdmin;
