import "./App.css";
import "./css/custom.css";
import "./css/style.default.css";

import { BrowserRouter, Route, Switch } from "react-router-dom";

import Footer from "./Share/Footer/Footer";
import Header from "./Share/Header/Header";
import Home from "./Home/Home";
import Detail from "./Detail/Detail";
import Cart from "./Cart/Cart";
import SignIn from "./Authentication/SignIn";
import SignUp from "./Authentication/SignUp";
import Checkout from "./Checkout/Checkout";
import History from "./History/History";
import Shop from "./Shop/Shop";
import Chat from "./Share/Chat/Chat";
import Admin from "./admin/Admin";
import RouterPrivate from "./router/RouterPrivate";
import Sidebar from "./admin/compoments/Sidebar";
import User from "./admin/page/user/User";
import ProductAdmin from "./admin/page/product/ProductAdmin";
import PageNotFound from "./Shop/404/PageNotFound";

function App() {
  const user = localStorage.getItem("id_user");

  return (
    <div className="App">
      <BrowserRouter>
        <Header />
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/detail/:id" component={Detail} />
          <Route path="/cart" component={Cart} />
          {/* ko có user mới đăng ký và đăng nhập */}
          {user && (
            <>
              <Route path="/signin" component={SignIn} />
              <Route path="/signup" component={SignUp} />
              <Route path="/history" component={History} />
            </>
          )}
          <Route path="/checkout" component={Checkout} />
          <Route path="/shop" component={Shop} />
          <Route path="/chat" component={Chat} />

          {/* router Private admin */}
          <RouterPrivate path="/admin/products" component={ProductAdmin} />
          <RouterPrivate path="/admin/users" component={User} />
          <RouterPrivate path="/admin" component={Admin} />
        </Switch>
      </BrowserRouter>

      <Chat />

      <Footer />
    </div>
  );
}

export default App;
